// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

/**
 * @title TherapyToken
 * @dev Native implementation of ERC20 token with staking and crisis management
 */
contract TherapyToken {
    // ============ ERC20 Basic Variables ============
    string public constant name = "Therapy Token";
    string public constant symbol = "THERAPY";
    uint8 public constant decimals = 18;
    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    // ============ Ownership & Security ============
    address private _owner;
    bool private _locked; // Reentrancy guard

    // ============ Structs ============
    struct StakingPosition {
        uint256 amount;
        uint256 startTime;
        uint256 lockPeriod;
        uint256 lastRewardCalculation;
        uint256 accumulatedRewards;
    }

    struct UserMetrics {
        uint256 sessionCount;
        uint256 progressScore;
        uint256 communityContributions;
        uint256 reputationScore;
        uint256 lastActivityTimestamp;
    }

    struct CrisisInfo {
        bool isActive;
        uint256 riskLevel;
        uint256 allocatedFunds;
        uint256 lastAssessment;
        address[] emergencyContacts;
        uint256 responseTime;
    }

    // ============ State Variables ============
    mapping(address => StakingPosition) public stakingPositions;
    mapping(address => UserMetrics) public userMetrics;
    mapping(address => CrisisInfo) public crisisInfo;

    uint256 public constant MIN_STAKE_PERIOD = 30 days;
    uint256 public constant BASE_APY = 500; // 5% (basis points)
    uint256 public constant MAX_APY = 2500; // 25%
    uint256 public constant MAX_PROGRESS_SCORE = 100;
    uint256 public constant REPUTATION_MULTIPLIER = 100;
    uint256 public constant CRISIS_FUND_THRESHOLD = 1000 * 10**18;
    uint256 public constant MAX_RISK_LEVEL = 10;

    uint256 public totalStaked;
    uint256 public crisisFundBalance;

    // ============ Events ============
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event Staked(address indexed user, uint256 amount, uint256 lockPeriod);
    event Unstaked(address indexed user, uint256 amount, uint256 rewards);
    event RewardsClaimed(address indexed user, uint256 amount);
    event ProgressUpdated(address indexed user, uint256 newScore);
    event CrisisTriggered(address indexed user, uint256 riskLevel);
    event EmergencyFundsAllocated(address indexed user, uint256 amount);
    event CommunityContribution(address indexed user, uint256 amount);

    // ============ Modifiers ============
    modifier onlyOwner() {
        require(msg.sender == _owner, "Not owner");
        _;
    }

    modifier nonReentrant() {
        require(!_locked, "Reentrant call");
        _locked = true;
        _;
        _locked = false;
    }

    // ============ Constructor ============
    constructor() {
        _owner = msg.sender;
        _mint(msg.sender, 1000000 * 10**18); // Initial supply: 1M tokens
    }

    // ============ ERC20 Functions ============
    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 amount) public returns (bool) {
        _transfer(msg.sender, to, amount);
        return true;
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address from, address to, uint256 amount) public returns (bool) {
        require(_allowances[from][msg.sender] >= amount, "Insufficient allowance");
        _allowances[from][msg.sender] -= amount;
        _transfer(from, to, amount);
        return true;
    }

    // ============ Therapy Progress & Rewards ============
    function updateProgress(address user, uint256 progressScore) external onlyOwner {
        require(progressScore <= MAX_PROGRESS_SCORE, "Invalid progress score");

        UserMetrics storage metrics = userMetrics[user];
        metrics.progressScore = progressScore;
        metrics.sessionCount += 1;
        metrics.lastActivityTimestamp = block.timestamp;

        uint256 rewards = calculateProgressRewards(progressScore);
        if (rewards > 0) {
            _mint(user, rewards);
        }

        emit ProgressUpdated(user, progressScore);
    }

    function recordCommunityContribution(address user, uint256 contributionValue) external onlyOwner {
        UserMetrics storage metrics = userMetrics[user];
        metrics.communityContributions += contributionValue;
        metrics.reputationScore = calculateReputationScore(metrics);

        uint256 rewards = (contributionValue * REPUTATION_MULTIPLIER) / 100;
        _mint(user, rewards);

        emit CommunityContribution(user, contributionValue);
    }

    // ============ Staking Functions ============
    function stake(uint256 amount, uint256 lockPeriod) external nonReentrant {
        require(amount > 0, "Cannot stake 0");
        require(lockPeriod >= MIN_STAKE_PERIOD, "Lock period too short");
        require(_balances[msg.sender] >= amount, "Insufficient balance");

        StakingPosition storage position = stakingPositions[msg.sender];

        if (position.amount > 0) {
            _claimRewards(msg.sender);
        }

        _transfer(msg.sender, address(this), amount);

        position.amount += amount;
        position.startTime = block.timestamp;
        position.lockPeriod = lockPeriod;
        position.lastRewardCalculation = block.timestamp;

        totalStaked += amount;

        emit Staked(msg.sender, amount, lockPeriod);
    }

    function unstake() external nonReentrant {
        StakingPosition storage position = stakingPositions[msg.sender];
        require(position.amount > 0, "No staking position");
        require(
            block.timestamp >= position.startTime + position.lockPeriod,
            "Lock period not ended"
        );

        uint256 rewards = calculateStakingRewards(msg.sender);
        uint256 totalAmount = position.amount + rewards;

        uint256 stakedAmount = position.amount;
        position.amount = 0;
        position.accumulatedRewards = 0;
        totalStaked -= stakedAmount;

        _mint(msg.sender, rewards);
        _transfer(address(this), msg.sender, stakedAmount);

        emit Unstaked(msg.sender, stakedAmount, rewards);
    }

    // ============ Crisis Management ============
    function triggerCrisis(address user, uint256 riskLevel) external onlyOwner {
        require(riskLevel <= MAX_RISK_LEVEL, "Invalid risk level");

        CrisisInfo storage crisis = crisisInfo[user];
        crisis.isActive = true;
        crisis.riskLevel = riskLevel;
        crisis.lastAssessment = block.timestamp;
        crisis.responseTime = calculateResponseTime(riskLevel);

        uint256 allocation = calculateCrisisAllocation(riskLevel);
        require(crisisFundBalance >= allocation, "Insufficient crisis fund");

        crisis.allocatedFunds = allocation;
        crisisFundBalance -= allocation;

        emit CrisisTriggered(user, riskLevel);
        emit EmergencyFundsAllocated(user, allocation);
    }

    // ============ Internal Functions ============
    function _transfer(address from, address to, uint256 amount) internal {
        require(from != address(0), "Transfer from zero");
        require(to != address(0), "Transfer to zero");
        require(_balances[from] >= amount, "Insufficient balance");

        _balances[from] -= amount;
        _balances[to] += amount;
        emit Transfer(from, to, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "Mint to zero");
        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "Approve from zero");
        require(spender != address(0), "Approve to zero");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _claimRewards(address user) internal {
        uint256 rewards = calculateStakingRewards(user);
        if (rewards > 0) {
            stakingPositions[user].lastRewardCalculation = block.timestamp;
            stakingPositions[user].accumulatedRewards = 0;
            _mint(user, rewards);
            emit RewardsClaimed(user, rewards);
        }
    }

    function calculateStakingRewards(address user) internal view returns (uint256) {
        StakingPosition storage position = stakingPositions[user];
        if (position.amount == 0) return 0;

        uint256 duration = block.timestamp - position.lastRewardCalculation;
        uint256 apy = calculateUserAPY(user);

        return (position.amount * apy * duration) / (365 days) / 10000 + position.accumulatedRewards;
    }

    function calculateUserAPY(address user) internal view returns (uint256) {
        UserMetrics storage metrics = userMetrics[user];
        uint256 baseApy = BASE_APY;

        if (metrics.progressScore > 80) {
            baseApy += 500; // +5%
        }
        if (metrics.reputationScore > 90) {
            baseApy += 500; // +5%
        }

        return baseApy > MAX_APY ? MAX_APY : baseApy;
    }

    function calculateProgressRewards(uint256 progressScore) internal pure returns (uint256) {
        return (progressScore * 10**18) / 10; // 0.1 token per progress point
    }

    function calculateReputationScore(UserMetrics memory metrics) internal pure returns (uint256) {
        uint256 score = (metrics.progressScore + 
            metrics.communityContributions + 
            (metrics.sessionCount * 5)) / 3;
        return score > 100 ? 100 : score;
    }

    function calculateResponseTime(uint256 riskLevel) internal pure returns (uint256) {
        if (riskLevel >= 8) return 1 hours;
        if (riskLevel >= 5) return 12 hours;
        return 24 hours;
    }

    function calculateCrisisAllocation(uint256 riskLevel) internal pure returns (uint256) {
        return (CRISIS_FUND_THRESHOLD * riskLevel) / MAX_RISK_LEVEL;
    }
}

